class questionList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
      isDisplay: true,
    };
  }

  render() {
    let answers = [];
    for (let i = 0; i < this.props.quiz.answers.length; i++) {
      if (parseInt(this.props.arrQuestion.correct) === i) {
        answers.push(
          <QuestionChoice
            choice={i}
            handleClick={this.props.handleClick}
            answer={this.props.quiz.answers[i]}
            image={this.props.image}
            picked={true}
          />
        );
      } else {
        answers.push(
          <QuestionChoice
            choice={i}
            handleClick={this.props.handleClick}
            answer={this.props.quiz.answers[i]}
            image={this.props.image}
          />
        );
      }
    }
    return (
      <div className="w-full h-full">
        {/* <image className='relative' src= {this.props.image ? this.props.image : ''}/>    */}
        <image src='https://i.ytimg.com/vi/JkjwRZ48tjI/maxresdefault.jpg'></image>
        <p className="font-bold text-white">
          CÃ¢u {this.props.stt}: {this.props.question}
        </p>
        {answers}
      </div>
    );
  }
}
export default questionList;
