import React from "react";

class QuestionChoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }
  showColor = (color) => {
    return {
      color: color,
    };
  };

  changeText = () => {
    this.props.handleClick(this.props.choice,this.props.stt);
    this.state.color === "white" ? this.setState({color :'lightgreen'}) : this.setState({color :'white'})
    };


  render() {
    return (
      <div className="px-4 text-white">
        <input type="checkbox" name="gender" onClick={this.changeText}  />&nbsp;
        <p className='inline'  style={{ color: this.state.color }}>{this.props.answer}</p>&nbsp;   
      </div>
    );
  }
}
export default QuestionChoice;
