import React from "react";
import QuestionChoice from "./questionChoice";

class questionList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }
  showColor = (a) => {
    return {
      color: a,
    };
  };

  changeText = () => {
    this.state.color === "white" ? this.setState({color :'green'}) : this.setState({color :'white'})
  };

  render() {
    let { answers, question, stt } = this.props;
    let elms = answers.map((question, index) => {
      return (
        <div className="px-4 text-white">
          <input
            type="checkbox"
            name={question}
            onClick={this.changeText}
          ></input>
          &nbsp;
          <span style={{ color: this.state.color }}>{question}</span>
        </div>
      );
    });
    return (
      <div className="w-full h-full">
        <p className="font-bold text-white">
          {stt}.{question}
        </p>
        {elms}
      </div>
    );
  }
}
export default questionList;
