import React from "react";

class QuestionChoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
      picked: this.props.picked,
    };
  }
  showColor = (color) => {
    return {
      color: color,
    };
  };

  changeText = () => {
    this.props.handleClick(this.props.choice);
    this.state.color === "white"? this.setState({ color: "lightgreen" }) : this.setState({ color: "white" });
    this.setState({
        picked: this.state.picked === true ? false : true,
      },
      console.log(this.state.picked)
    );
  };

  render() {
    return (
      <div className="px-4 text-white">
        <input
          type="checkbox"
          name="gender"
          onClick={this.changeText}
          checked={this.state.picked}
        />
        &nbsp;
        <p className="inline" style={{ color: this.state.color }}>
          {this.props.answer}
        </p>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
