import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { seconds: 10, minutes: 1 };
  }
  time = () => {
    this.setState({
      seconds: this.state.seconds - 1,
    });
    if (this.state.seconds < 0) {
      this.setState({
        seconds: 10,
        minutes: this.state.minutes - 1,
      });
    }
  };
  componentDidMount() {
    setInterval(this.time, 1000);
    if (this.state.seconds === 0) clearInterval(this.time);
  }
  render() {
    return (
      <div class="w-32 bg-white tracking-wide text-gray-800 font-bold rounded  shadow-md py-2">
        <div class="mx-auto text-center">
          <i class="fas fa-history"></i> {this.state.minutes}:
          {this.state.seconds}
        </div>
      </div>
    );
  }
}

export default Clock;
