import React from "react";

class QuestionChoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }
  showColor = (color) => {
    return {
      color: color,
    };
  };

  changeText = () => {
    this.state.color === "white"
      ? this.setState({ color: "lightgreen" })
      : this.setState({ color: "white" });
    this.props.correct === this.props.question ? alert(true) : alert(false);
  };

  render() {
    return (
      <div className="px-4 text-white">
        <input
          type="checkbox"
          name="gender"
          value={this.props.question}
          onClick={this.changeText}
        >
          <button
            onClick={() => this.props.handleClick(this.props.choice)}
            style={{ color: this.state.color }}
          >
            {this.props.answer}
          </button>
        </input>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
