import React from "react";
import QuestionChoice from "./questionChoice";
import bg from "../background.jpg";

// class Question extends React.Component {
//   var style = {
//     color: "red",
//   }
//   return (
//     <h1 style={style}>{props.quiz.question}</h1>
//   )
// }

class questionList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
      isDisplay: true,
    };
  }

  render() {
    let answers = [];
    for (let i = 0; i < this.props.quiz.answers.length; i++) {
      if (parseInt(this.props.arrQuestion.correct) === i) {
        answers.push(
          <QuestionChoice
            choice={i}
            handleClick={this.props.handleClick}
            answer={this.props.quiz.answers[i]}
            picked={true}
          />
        );
      } else {
        answers.push(
          <QuestionChoice
            choice={i}
            handleClick={this.props.handleClick}
            answer={this.props.quiz.answers[i]}
          />
        );
      }
    }
    return (
      <div className="w-full h-full">
        <img alt='Ảnh' className={this.props.image ? "bg-cover max-w-xs m-2" :"hidden"} src= {this.props.image ? this.props.image : 'null'}/> 
        <p className="font-bold text-white">
        <span className="text-sm font-medium bg-green-100 py-1 px-2 rounded text-green-500 align-middle">Câu {this.props.stt}</span>
           &nbsp;{this.props.question}
        </p>
        {answers}
      </div>
    );
  }
}
export default questionList;
