import React from "react";

class questionItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: 'white',
    };
  }
  setText = () => {
    return { color : 'green'}
  };

  render() {
    let { answers, question, stt } = this.props;
    let elms = answers.map((question, index) => {
      return (
        <div className="px-4">
          <input type="checkbox" name={question} onClick={this.setText} />{" "}
          &nbsp;
          <span style={this.state.color}>{question}</span>
        </div>
      );
    });
    return (
      <div className="w-full h-full">
        <p className="font-bold">
          {stt}.{question}
        </p>
        {elms}
      </div>
    );
  }
}
export default questionItem;
