import React from "react";

class QuestionChoice extends React.Component {

  showColor = (color) => {
    return {
      color: color,
    };
  };

  changeText = () => {
    this.props.handleClick(this.props.choice);
  };

  render() {
  
    return (
      <div className="px-4 text-white">
        <input
          type="checkbox"
          name="gender"
          onClick={this.changeText}
          checked={this.props.picked }
        />
        &nbsp;
        <p className="inline" style={{ color: this.state.color }}>
          {this.props.answer}
        </p>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
