import React from "react";
import Swal from 'sweetalert2'

class Result extends React.Component {
  // constructor(props){
  //   super(props);
  //   this.state = {
  //     picked : this.props.picked
  //   }
  // }

  changeText = () => {
    this.props.handleClick(this.props.choice);
  };

  Result =(e) =>{
    Swal.fire({
      title: 'Bạn muốn nộp bài ?',
      text: "You won't be able to revert this!",
      icon: 'success',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Chấp nhận'
    }).then((result) => {
      if (result.value) {
        if(e > 10)
        Swal.fire(
          'Điểm của bạn là '+e,
          'Chúc mừng ! Bạn đã đậu !!',
          'success'
        )
        else
        Swal.fire({
          icon: 'error',
          title: 'Điểm của bạn là '+e,
          text: 'Tiếc quá ! Bạn không đậu !! ',
        })
      }
    })

 
  }

  render() {
    return (
      <div className="flex justify-content mt-8 w-1/2">
        <button
          type="submit"
           className="block uppercase mx-auto shadow bg-indigo-600 hover:bg-indigo-500 focus:shadow-outline focus:outline-none text-white text-xs py-3 px-10 rounded relative"
          onClick={()=>{this.Result(this.props.score)}}
          >Submit
        </button>
      </div>
    );
  }
}
export default Result;
