  //   }
  // }
  

  handleClick = (a) => {
    let e = a.target.value;
    this.props.handleClick(this.props.choice,this.props.picked);
  };

  render() {
  
    return (
      <div className="px-4 text-white">
        <input
          type="checkbox"
          name="gender"
          onClick={this.handleClick}
          checked={this.props.picked }
        />
        &nbsp;
        <p className="inline" >
          {this.props.answer}
        </p>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
