import React from "react";
import bg from "./background.jpg";
import "./App.css";
import QuestionsList from "./components/questionList";
import Control from "./components/control";
import Countdown from "react-countdown";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      data: [
        {
          stt: 1,
          question: "Khái niệm “đường bộ” được hiểu như thế nào là đúng: ",
          answers: [
            "Đường bộ, cầu đường bộ.",
            "Hầm đường bộ, bến phà đường bộ.",
            "Hầm đường bộ, bến phà đường bộ.",
          ],
          correct: "Đường bộ, cầu đường bộ.",
        },
        {
          stt: 2,
          question: "“Vạch kẻ đường” được hiểu như thế nào là đúng?",
          answers: [
            "Vạch kẻ đường là vạch chỉ sự phân chia làn đường, vị trí hoặc hướng đi, vị trí dừng lại.",
            "Vạch kẻ đường là vạch chỉ sự phân biệt trí dừng, đỗ trên đường.",
            "Tất cả các ý nêu trên.",
          ],
          correct: "Tất cả các ý nêu trên.",
        },
      ],
      score: 0,
      responses: 0,
    };
  }

  getQuestions = () => {
    this.setState(
      {questions: this.state.data} 
    );
    localStorage.setItem("data", JSON.stringify(this.state.data));
  };

  componentWillMount() {
    this.getQuestions();
  }

  computeAnswer = (answer, correctAns) => { 
    if (answer === correctAns) { 
      this.setState({ 
        score: this.state.score + 1 
      }); 
    } 
    this.setState({ 
      responses: this.state.responses < 5 ? this.state.responses + 1 : 5 
    }); 
  }; 

  render() {
    const renderer = ({ minutes, seconds }) => {
      return (
        <span>
          {minutes}:{seconds}
        </span>
      );
    };

    let elm = this.state.questions.map(({ question, answers, correct, stt }) =>{
    return (
          <QuestionsList
            question={question}
            answers={answers}
            stt={stt}
            choice = {this.computeAnswer}
            correct ={correct}
          />
        )}
      )
      let elms = this.state.questions.map(({ stt }) =>{
        return (
              <Control
                stt={stt}
              />
            )}
          )
    return (
      <div className="text-black relative font-sans ">
        <div className=" bg-black h-screen absolute top-0">
          <img src={bg} className="bg-cover h-full h-full opacity-25 top-0" />
        </div>
        <div className="container mx-auto py-10  relative">
          <h1 className="text-4xl text-center text-gray-100	uppercase font-mono ">
            Thi trắc nghiệm bằng lái xe A1
          </h1>
        </div>
        <div className=" container mx-auto w-full relative py-8">
          <div className=" px-8 flex flex-wrap flex-column justify-between items-center">
            <div class="w-24 bg-white tracking-wide text-gray-800 font-bold rounded  shadow-md py-2">
              <div class="mx-auto text-center">
                <i class="fas fa-file"></i> Đề
              </div>
            </div>
            <div class="w-32 bg-white tracking-wide text-gray-800 font-bold rounded  shadow-md py-2">
              <div class="mx-auto text-center">
                <i class="fas fa-history"></i>  
                <Countdown date={Date.now() + 899000} renderer={renderer} />
              </div>
            </div>
          </div>
        </div>
        <div className="flex  container w-full mx-auto relative justify-between">
          <div className=" w-4/12 flex flex-row">
            {elms}
          </div>
          <div className=" w-7/12 flex flex-col">
             <div className="  p-2">{elm}</div>
          </div>
        </div>
      </div>
    );
  }
}
export default App;
