import React from "react";
import QuestionChoice from "./questionChoice";

class questionList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }

  render() {
    let { answers, question, stt } = this.props;
    let elms = answers.map((question, index) => {
      return (
       <QuestionChoice question={question} key={index} correct={this.props.correct}/>
      );
    });
    return (
      <div className="w-full h-full">
        <p className="font-bold text-white">
          {stt}.{question}
        </p>
        {elms}
      </div>
    );
  }
}
export default questionList;
