import React from "react";

class QuestionChoice extends React.Component {

  // constructor(props){
  //   super(props);
  //   this.state = {
  //     picked : this.props.picked
  //   }
  // }


  handleClick = () => {
    this.props.handleClick(this.props.choice);
  };

  render() {
  
    return (
      <div className="px-4 text-white">
        <input
          type="radio"
          name="gender"
          onClick={this.handleClick}
          checked={this.props.picked ? true: false }
        />
        &nbsp;
        <p className="inline" >
          {this.props.answer}
        </p>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
