import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { seconds: 60, minutes: 15 };
  }
  time = () => {
    this.setState({
      seconds: this.state.seconds - 1,
    });
    if (this.state.seconds < 0) {
      this.setState({
        seconds: 60,
        minutes: this.state.minutes - 1,
      });
    }
  };
  componentDidMount() {
    this.intervalId = setInterval(this.time, 1000);
  }
  render() {
    return (
      <div class="w-32 bg-white tracking-wide text-gray-800 font-bold rounded border-b-2 border-blue-500  shadow-md py-2 px-6 inline-flex items-center">
        <div class="mx-auto">{this.state.minutes}:{this.state.seconds}</div>
       <span className=' w-16 h-16 relative'><i class="fas fa-history"></i></span>
      </div>
    );
  }
}

export default Clock;
