import React from "react";

class questionItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }
  showColor = (a) => {
    return {
      color: a,
    };
  };

  changeText = () => {
    this.setState({
      color: "lightgreen",
    });
  };

  render() {
    let { answers, question, stt } = this.props;
    let elms = answers.map((question, index) => {
      return (
        <div className="px-4 text-white">
          <input  type="checkbox" name={question} checked={this.changeText} ></input>
          &nbsp;
          <span style={{color:this.state.color}} >{question}</span>
        </div>
      );
    });
    return (
      <div className="w-full h-full">
        <p className="font-bold text-white">
          {stt}.{question}
        </p>
        {elms}
      </div>
    );
  }
}
export default questionItem;
