import React from "react";

class QuestionChoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "white",
    };
  }
  showColor = (color) => {
    return {
      color: color,
    };
  };

  changeText = () => {
    this.state.color === "white" ? this.setState({color :'lightgreen'}) : this.setState({color :'white'})
  };

  render() {
    return (
      <div className="px-4 text-white">
        <input
          type="radio"
          onClick={this.changeText}
        ></input>
        &nbsp;
        <span style={{ color: this.state.color }}>{this.props.question}</span>
      </div>
    );
  }
}
export default QuestionChoice;
