import React from "react";

class QuestionChoice extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      picked : this.props.picked
    }
  }


  changeText = () => {
    this.props.handleClick(this.props.choice);
  };

  render() {
  
    return (
      <div className="px-4 text-white">
        <input
          type="checkbox"
          name="gender"
          onClick={this.changeText}
          checked={this.state.picked }
        />
        &nbsp;
        <p className="inline" >
          {this.props.answer}
        </p>
        &nbsp;
      </div>
    );
  }
}
export default QuestionChoice;
