import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { seconds: 60, minutes: 15 };
  }
  time = () => {
    this.setState({
      seconds: this.state.seconds - 1,
    });
    if (this.state.seconds < 0) {
      this.setState({
        seconds: 60,
        minutes: this.state.minutes - 1,
      });
    }
  };
  componentDidMount() {
    this.intervalId = setInterval(this.time, 1000);
  }
  render() {
    return (
      <div class="w-32 bg-white tracking-wide text-gray-800 font-bold rounded border-b-4 border-blue-500 shadow-md py-2 pr-6">
        <div class="mx-auto text-center">
        <i class="fas fa-history"></i>  {this.state.minutes} : {this.state.seconds}
        </div>
      </div>
    );
  }
}

export default Clock;
