        {
          stt: "1",
          title: "Khái niệm “đường bộ” được hiểu như thế nào là đúng: ",
          dapan: [
            { name: "Đường bộ, cầu đường bộ.", status: "true" },
            { name: "Hầm đường bộ, bến phà đường bộ.", status: "false" },
            { name: "Hầm đường bộ, bến phà đường bộ.", status: "false" },
          ],
        },
        {
          stt: "2",
          title: "“Vạch kẻ đường” được hiểu như thế nào là đúng?",
          dapan: [
            { name: "Vạch kẻ đường là vạch chỉ sự phân chia làn đường, vị trí hoặc hướng đi, vị trí dừng lại.", status: "true" },
            { name: "Vạch kẻ đường là vạch chỉ sự phân biệt trí dừng, đỗ trên đường.", status: "false" },
            { name: "Tất cả các ý nêu trên.", status: "false" },
          ],
        },
      ],
    };
  }

