import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { seconds: 10 , minutes : 5};
  }
  time = () => {
    this.setState({
      seconds: this.state.seconds - 1,

    });
    if (this.state.seconds < 0) {
      this.setState({
        seconds: 10,
        minutes: this.state.secminutesonds - 1,
      });
    }
  };
  componentDidMount() {
    this.intervalId = setInterval(this.time, 1000);
  }
  render() {
  return <div>{this.state.minutes} : {this.state.seconds}</div>;
  }
}

export default Clock;
